import { useState } from 'react';
import './App.css'



function App() {
  const [count, setCount] = useState(0);
  function handleClick() {
    setCount(count + 1);

  }

  return (
    <div className="intro"> 
      <h1>Welcome to my App!</h1> 
        <button className="button" onClick={handleClick}> Click me more! \n Clicks: {count} </button>
    </div>
  );
}

export default App;
